import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function ReviewEdit(props) {
  const [reviewData, setReviewData] = useState({
    content: "",
  });

  const { content } = reviewData;
  const { bookId, id } = useParams();
  const { handleOpen, reviewUpdate, reviews } = props;

  useEffect(() => {
    const prefillReviewData = () => {
      const reviewItem = reviews.find((review) => review?.id === Number(id));
      console.log(reviewItem)
      setReviewData({
        content: reviewItem?.content,
      });
    };
    if (reviews?.length) {
      prefillReviewData();
    }
  }, [reviews, id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setReviewData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div className="modal-container" onClick={(e) => handleOpen(false)}>
    <div className="modal" onClick={(e) => e.stopPropagation()}>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            reviewUpdate(bookId, id, reviewData);
          }}
        >
          <label>
            Edit Review:
            <textarea
              type="text"
              rows={10}
              cols={78}
              name="content"
              value={content}
              onChange={handleChange}
            />
          </label>

        </form>
      </div>
      </div>
  );
}

export default ReviewEdit;
