import { useState } from "react";

function ReviewCreate(props) {
  const { book, reviewCreate, currentUser } = props;
  // const currentBook = book?.id
  // const userData = currentUser?.id
  const [formData, setFormData] = useState({
    content: "",
  });
  const { content } = formData;



  const handleChange = (e) => {
  const { book, reviewCreate, currentUser } = props;
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
      [user_id]: currentUser?.id,
      [book_id]: book?.id,
    }));
  };
  return (
    <form
      onSubmit={(e) => {
        e.preventDefault();
        reviewCreate(book?.id, formData);
      }}
    >
      <label>
        What did you think of {book?.title}?
        <input
          type="text" name="content" value={content}
          onChange={handleChange}
        />
      </label>
      <button>POST</button>
    </form>
  );
}

export default ReviewCreate;
