import React from 'react';

function Search(props) {
  const { onSubmit, onChange } = props;

  return (
    <form onSubmit={(e) => { onSubmit(e) }}>
      <label>
      SEARCH:    
      <input
        type="text"
        name="search"
        placeholder="search by title or author"
        value={props.value}
        onChange={(e) => {onChange(e)}}
        />
        </label>
    </form>
  );
}

export default Search;
