import React, {useState} from 'react';
import { Link } from "react-router-dom"
import ReviewEdit from "./ReviewEdit"
// import Modal from "../components/Modal"

function Reviews(props) {
  const [open, handleOpen] = useState(false)
  const { reviews, currentUser, reviewUpdate, reviewDelete, book } = props;
  console.log(reviews)
  return (
    <div>
      {reviews.map((review) => (
        <div className="review" key={review.id}>
          <div>
          <h6>{review?.user_id === currentUser?.id ? review.user?.username : null}</h6>
          {currentUser?.id === review?.user_id && (
            <div>
            <Link to={`/books/${review.book_id}/reviews/${review.id}`}>
              <button className="edit" onClick={() => handleOpen(review.id)}>EDIT</button>
            </Link>
              <button onClick={() => reviewDelete(book?.id, review.id)}>DELETE</button>
              </div>
            )}
            </div>
          <p>{review.content}</p>
        </div>
      ))}
      {open && (
        <ReviewEdit
          reviews={reviews}
          open={open}
          handleOpen={handleOpen}
          reviewUpdate={reviewUpdate}
        />
      )}
    </div>
  );
}

export default Reviews;
