import { useState } from "react";
import { useHistory } from "react-router-dom"
import { postReview } from "../services/reviews"

function ReviewCreate(props) {
  const [formData, setFormData] = useState({
  content: "",
  })
  const { book, setReviews, id } = props;
  const history = useHistory

  const reviewCreate = async (reviewData) => {
    const newReview = await postReview(reviewData);
    setReviews(prevState => [...prevState, newReview])
    history.push(`/books/${id}`)
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }))
  }
  return (
    <form onSubmit={(e) => {
      e.preventDefault();
      reviewCreate(formData); 
    }}>
      <label>What did you think of {book.title}?
        <input>
          type="text"
          name="content"
          value={content}
          onChange={handleChange}
        </input>
      </label>
    </form>
  );
}

export default ReviewCreate;
