import React from 'react';
import {Link} from "react-router-dom"

function Books(props) {
  const { books, title, image_url } = props
  return (
    <div>
      <p>Books!</p>
      {books.map((book) => (
        <div key={book.id}>
          <Link to={`/books/${book.id}`}>
            <img src={image_url} alt="cover art"/>
            <p>{title}</p>
          </Link>
          {currentUser?.id === book.user_}
        </div>
      ))}
    </div>
  );
}

export default Books;
