import React, {useState} from "react";
import Search from "../components/Search"
import Book from "../components/Book"

function Books(props) {
  const { books, currentUser } = props;
  const [searchResults, setSearchResults] = useState([]); 




  const handleSearch = (e) => {
    const query = books.filter((book) => {
      if (book.title.toLowerCase().includes(e.target.value.toLowerCase) || book.author.toLowerCase().includes(e.target.value.toLowerCase)) {
        return book
      } else {
        return <p>try your search again</p>
      }
    })
    setSearchResults(query)
  }

  const handleSubmit = (e) => e.preventDefault(); 

  const booksJSX = searchResults.map((book) => 
    <Book book={book} currentUser={currentUser} title={book.title} image_url={book.image_url} author={book.author} summary={book.summary}/>
  )

  return (
    <div className="books-container">
      <Search onSubmit={handleSubmit} onChange={handleSearch}/>
      <div className="books">
        {booksJSX}
    </div>
      </div>
  );
}

export default Books;
