import React, {useState} from "react";
import { Link } from "react-router-dom";
import Card from "../components/Card";
import Search from "../components/Search"

function Books(props) {
  const { books, currentUser } = props;
  const [searchResults, setSearchResults] = useState([]); 

  const handleSearch = (e) => {
    const query = books.filter((book) => {
      book.title.toLowerCase().includes(e.target.value.toLowerCase) || book.author.toLowerCase().includes
    })
    setSearchResults(query)
  }

  const handleSubmit = (e) => e.preventDefault();

  const bookCard = searchResults.map((book) => {
    <div key={book.id}>
          {currentUser ? (
            <Link to={`/books/${book.id}`}>
              <Card title={book.title} image={book.image_url} />
            </Link>
          ) : (
            <Card title={book.title} image={book.image_url} />
            )}
        </div>
  })


  return (
    <div className="books-container">
      <Search onSubmit={handleSubmit} onChange={handleSearch}/>

    <div className="books">
      {books.map((book) => (
        <div key={book.id}>
          {currentUser ? (
            <Link to={`/books/${book.id}`}>
              <Card title={book.title} image={book.image_url} />
            </Link>
          ) : (
            <Card title={book.title} image={book.image_url} />
            )}
        </div>
      ))}
    </div>
      </div>
  );
}

export default Books;
