import { useState } from "react";

function ReviewCreate(props) {
  const [formData, setFormData] = useState({
    content: "",
    user_id: "",
    book_id: ""
  });
  const { content, user_id, book_id } = formData;
  const { book, reviewCreate, currentUser } = props;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };
  return (
    <form
      onSubmit={(e) => {
        e.preventDefault();
        reviewCreate(book.id, currentUser.id, formData);
      }}
    >
      <label>
        What did you think of {book?.title}?
        <input
          type="text" name="content" value={content}
          onChange={handleChange}
        />
      </label>
      <label>User
        <input
          type="text" name="user_id" value={user_id}
          onChange={handleChange}
        />
      </label>
      <label>Book
        <input
          type="text" name="book_id" value={content}
          onChange={handleChange}
        />
      </label>
      <button>POST</button>
    </form>
  );
}

export default ReviewCreate;
