import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { getOneBook } from "../services/books";
// import { getAllReviews } from "../services/reviews";

import ReviewsContainer from "../containers/ReviewsContainer"
// import Reviews from "../components/Reviews"
// import ReviewCreate from "../components/ReviewCreate";

function BookDetails(props) {
  const [book, setBook] = useState(null);
  // const [reviews, setReviews] = useState([]);
  const { id } = useParams();
  const {currentUser, handleDelete} = props

  useEffect(() => {
    const fetchBook = async () => {
      const bookData = await getOneBook(id);
      setBook(bookData);
    };
    fetchBook();
  }, []);

  // useEffect(() => {
  //   const fetchReviews = async (bookId) => {
  //     const reviewData = await getAllReviews(bookId);
  //     setReviews(reviewData);
  //   };
  //   fetchReviews(id);
  // }, []);

  return (
    <>
      <div className="book-details">
        <img src={book?.image_url} alt={book?.title} />
        <h3>{book?.title}</h3>
        <h4>by {book?.author}</h4>
        <p>{book?.summary}</p>
      </div>
      {currentUser?.id === book?.user_id && (
        <>
          <Link to={`/books/${id}/edit`}>
            <button>EDIT</button>
          </Link>
          <button onClick={() => handleDelete(book.id)}>DELETE</button>
        </>
      )}

      <div>Reviews!</div>
      <ReviewsContainer book={book} id={id} currentUser/>
      {/* <ReviewCreate book={book} setReviews={setReviews} id={id}/>
      <Reviews
        reviews={reviews}
      /> */}
      </>
  );
}

export default BookDetails;
