import React from 'react';
// import ReviewEdit from "../components/ReviewEdit"

function Modal(props) {
  const { open, handleOpen, reviewUpdate } = props

  return (
    <div className="modal-container" onClick={(e) => handleOpen(false)}>
      <div className="" onClick={(e) => e.stopPropagation()}>
        <p>Edit Review</p>
        <button onClick={() => { reviewUpdate(open); handleOpen(false);}}>UPDATE</button>
        <button onClick={() => handleOpen(false)}>CANCEL</button>
      </div>
    </div>
  );
}

export default Modal;
